import { useState } from 'react';
import './App.css';
import { Page1 } from './components/Page1'
import { Page2 } from './components/Page2'

const App = () => {
  const [showSquare, setShowSquare] = useState(true);
  // hooks useState and useEffect are my friend
  const [inputName, setName] = useState('');
  const [inputStrain, setStrain] = useState('');


  /*
  const handleChangeName = event => {
    setName(event.target.value);
  };
  */


  /*
  useEffect(() => {

  })

  const handleChangeName = (val) => {
    setName(val);
  }
  */

  const handleClick = () => {
    if (!showSquare) {
      setName("");
    }
    setShowSquare(!showSquare);
  }

  const Screen = () => {
    if (showSquare) {
      return <Page1 onClickNext={handleClick} addName={setName} addStrain={setStrain}/>
      //return <Page1 onClickNext={handleClick}/>
    } else {
      return <Page2 onClickBack={handleClick} savedName={inputName} savedStrain={inputStrain}/>
      //return <Page2 onClickBack={handleClick}/>
    }
  }

  return (
    <div className="App">
      <Screen />
    </div>
  );
}

export default App;
